function Mahdi () {
  {
    const [input1, setInput1]=useState(
        {
            title:"",
             year:"",
             genre:""
        }
    );


    const handleChange = e => { 
        const {name, value}=e.target
        // e.target.name=e.target.value 
        setInput1({
            ...input1,
            [name]:value
        });
    };

    const handlesubmit=(e)=>{

        e.preventDefault();
        axios.post(`http://localhost:3004/posts`, input1).then((res) => {
