    const getfilm = ()=>{
      axios.get('http://localhost:3004/posts')
           .then ((response) => setFilme({posts: response.data}))
           .catch(error => console.log(error))
} 

    useEffect(()=>
    getfilm(),[]);


  return (
   /*filter(el=>el.title.toLowerCase().includes(input.toLowerCase()))*/
