import {useState} from 'react';
import axios from "axios";
import React, { useEffect, useLayoutEffect } from 'react';





function Mahdi () {
  {
    const [input1, setInput1]=useState(
        {
            title:"",
             year:"",
             description:""
        }
    );


    const handleChange = e => { 
        const {name, value}=e.target
        // e.target.name=e.target.value 
        setInput1({
            ...input1,
            [name]:value
        });
    };
/**********Get******************** */
const [movie,setMovie] = useState([])
    const getMovie = ()=>{
            axios.get('http://localhost:3000/posts').then((response) =>    
            setMovie(response.data) 
           ) 
    }

     useEffect( ()=>
      getMovie(),[]
     )

/*****************Post************ */
    const handlesubmit=(e)=>{

        e.preventDefault();
        axios.post(`http://localhost:3000/posts`, input1)
        .then((res) => {
          console.log(res)
        }); 
/***************Remove*********** */

            const handleRemove =(e)=>{
              e.preventDefault();
              axios.delete(`http://localhost:3000/posts/ ` )
              .then((res) => {
              console.log(res)

             });
             }

            }
    return (
        <div>
            <form onSubmit={handlesubmit} >
                <input value={input1.title} onChange={handleChange} type='text' name='title' placeholder='enter movie name' /> <br/>
                <input value={input1.year}  onChange={handleChange} type='text' name='year' placeholder='enter year release' /><br/>
                <input value={input1.genre}  onChange={handleChange} type='text' name='description' placeholder='enter description movie' /><br/>
                <button type="submit" >Submit</button>
                {/* <button type="remove" form="form1" onClick={handleRemove()} value="Remove">Delete</button>  */}
            </form>
        </div>



        )
}


}

